from random import randint
from math import gcd

flag = '''XXXXXXXXXXXXXXXXXXXXXXX'''

def keygen(LEN):
    R = randint(1,LEN)
    Ks = [[randint(0,26) for _ in range(randint(1,LEN))] for _ in range(R)]
    As = []
    while len(As) < R:
        A = []
        RR = randint(1,LEN)
        while len(A) < RR:
            a = randint(0,26)
            if gcd(a,26) == 1 :
                A.append(a)
        As.append(A)
    return (As,Ks)

def encrypt(M,As,Ks):
    for A,K in zip(As,Ks):
        C = ''
        LENA = len(A)
        LENK = len(K)

        for i in range(len(M)):
            C += chr((A[i % LENA] * (ord(M[i]) - ord('a')) + K[i % LENK]) % 26 + ord('a'))
        M = C
    return M

LEN = 5
As,Ks = keygen(LEN)
C = encrypt(flag,As,Ks)
print(C)

